mapper.update:
UPDATE TB_TCHR
   SET SBJT_CHRG = #{sbjtChrg},
       TCHR_INTRO = #{tchrIntro}
 WHERE TCHR_NO = #{tchrNo}

UPDATE TB_USR
   SET USR_TP = 'ROLE_STAFF'
 WHERE ID = #{id}

UPDATE TB_USR
   SET USR_TP = 'ROLE_TCHR'
 WHERE ID = #{id}

UPDATE TB_EMP
   SET ID = #{id},
       SLR = #{slr}
 WHERE EMP_NO = #{empNo}

UPDATE TB_EMP
   SET RETIRED_DT = #{retiredDt}
 WHERE EMP_NO = #{empNo}
